// File: src/pages/BookAppointmentPage.tsx
import React, { useState, useEffect } from 'react';
import { getAllServices } from '../api/serviceApi'; // Функція для отримання всіх послуг
import { createAppointment } from '../api/appointmentApi'; // Функція для створення бронювання

interface Service {
  id: number;
  title: string;
}

const BookAppointmentPage: React.FC = () => {
  const [doctorId, setDoctorId] = useState('');
  const [serviceId, setServiceId] = useState('');
  const [date, setDate] = useState('');
  const [services, setServices] = useState<Service[]>([]);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState<string | null>(localStorage.getItem('token') || ''); // Отримуємо токен

  useEffect(() => {
    getAllServices()
      .then(response => setServices(response.data)) // Отримуємо список послуг
      .catch(() => setMessage('Failed to load services.'));
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!token) {
      setMessage('Please log in to book an appointment.');
      return;
    }

    try {
      // Створення нового бронювання
      await createAppointment({ doctorId: Number(doctorId), serviceId: Number(serviceId), date }, token);
      setMessage('Appointment created successfully!');
      setDoctorId('');
      setServiceId('');
      setDate('');
    } catch (err) {
      setMessage('Failed to create appointment.');
    }
  };

  return (
    <div>
      <h1>Book an Appointment</h1>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Doctor ID:</label>
          <input
            type="number"
            value={doctorId}
            onChange={(e) => setDoctorId(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Service:</label>
          <select value={serviceId} onChange={(e) => setServiceId(e.target.value)} required>
            <option value="">Select a service</option>
            {services.map((service) => (
              <option key={service.id} value={service.id}>
                {service.title}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Date:</label>
          <input
            type="datetime-local"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
};

export default BookAppointmentPage;
