import React, { useEffect, useState } from 'react';
import { getAllDoctors } from '../api/doctorApi';
import { Link } from 'react-router-dom';

interface Doctor {
  id: number;
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  photoUrl: string;
  profile: {
    education: string;
    experience: string;
    bio: string;
    specialization: string;
    price: number;
    rating: number | null;
  };
}

const DoctorsPage = () => {
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const response = await getAllDoctors();
        setDoctors(response.data);
        setLoading(false);
        console.log(response.data);
      } catch (err) {
        setError('Не вдалося завантажити лікарів');
        setLoading(false);
      }
    };

    fetchDoctors();
  }, []);

  if (loading) return <div>Завантаження...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="doctors-page" style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>лікарі</h1>
      <div className="doctors-list">
        {doctors.map((doctor) => (
        <div
          key={doctor.id}
          className="relative rounded-xl border border-gray-200 bg-white shadow-md p-4 mb-6"
        >
          <div className="flex items-center gap-4">
            <img
              src={doctor.photoUrl}
              alt={`${doctor.firstName} ${doctor.lastName}`}
              className="w-24 h-24 rounded-full object-cover border-2 border-primary"
            />
            <div className="flex-1">
              <h2 className="text-lg font-semibold text-primary">
                {doctor.firstName} {doctor.lastName}
              </h2>
              <p className="text-secondary text-base font-medium">
                {doctor.profile.specialization}
              </p>
              <p className="text-sm text-gray-500 mt-1 line-clamp-2">
                {doctor.profile.bio}
              </p>
            </div>
          </div>

          {/* Rating and Review */}
          <div className="mt-4 bg-primary text-white rounded-lg p-3 relative">
            <div className="font-semibold">Андрій Ковальчук</div>
            <div className="flex items-center gap-1 mt-1">
              {[...Array(5)].map((_, i) => (
                <span
                  key={i}
                  className={`text-yellow-400 ${i < (doctor.profile.rating || 0) ? '' : 'opacity-30'}`}
                >
                  ★
                </span>
              ))}
            </div>
            <p className="text-sm mt-2">
              Відгук або короткий текст про доктора якщо не знайдеться буде можливість розгорнути і прочитати
            </p>
          </div>

          {/* Bottom row */}
          <div className="mt-4 flex justify-between items-center">
            <p className="text-sm font-semibold text-gray-600">
              Від: {doctor.profile.price} грн
            </p>
            <Link
              to={`/doctors/${doctor.id}`}
              className="bg-primary hover:bg-blue-800 transition text-white text-sm font-bold py-2 px-4 rounded-lg"
            >
              Записатися
            </Link>
          </div>
        </div>
        ))}
      </div>
    </div>
  );
};

export default DoctorsPage;
