import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getDoctorProfile } from '../api/doctorApi';

const DoctorProfilePage = () => {
  const { doctorId } = useParams<{ doctorId: string }>();  // Отримуємо doctorId з параметрів маршруту
  const [doctor, setDoctor] = useState<any>(null);

  useEffect(() => {
    const loadDoctorProfile = async () => {
      if (!doctorId || isNaN(Number(doctorId))) {
        console.error('Invalid doctorId:', doctorId);
        return;
      }

      try {
        const { data } = await getDoctorProfile(Number(doctorId));
        setDoctor(data);
      } catch (error) {
        console.error('Error fetching doctor profile:', error);
      }
    };

    loadDoctorProfile();
  }, [doctorId]);

  if (!doctor) return <div>Loading...</div>;  // Якщо профіль лікаря ще не завантажений

  return (
    <div>
      <h2>Dr. {doctor.firstName} {doctor.lastName}</h2>
      <p>Email: {doctor.email}</p>
      <p>Phone: {doctor.phone}</p>
      <p>Education: {doctor.profile?.education}</p>
      <p>Experience: {doctor.profile?.experience}</p>
      <p>Bio: {doctor.profile?.bio}</p>
      {doctor.profile?.photoUrl && <img src={doctor.profile.photoUrl} alt="Doctor" width={200} />}
    </div>
  );
};

export default DoctorProfilePage;
