import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Register from './components/auth/RegisterForm';
import Login from './components/auth/LoginForm';
import ServicesPage from './pages/ServicesPage';
import AppointmentPage from './pages/BookAppointmentPage';
import AdminServicesPage from './pages/AdminServicesPage';
import AdminCategoriesPage from './pages/AdminCategoriesPage';
import ServiceDetailsPage from './pages/ServiceDetailsPage';
import DoctorListPage from './pages/DoctorListPage';
import DoctorProfilePage from './pages/DoctorProfilePage';
import CreateDoctorProfilePage from './pages/CreateDoctorProfilePage';
import DoctorsPage from './pages/DoctorsPage';
import Navbar from './components/Navbar';

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(null);
  const [role, setRole] = useState<'admin' | 'doctor' | 'patient' | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
      try {
        const payload = JSON.parse(atob(storedToken.split('.')[1]));
        setRole(payload.role);
      } catch (err) {
        console.error('Помилка розшифровки токена:', err);
      }
    }
  }, []);

  const handleLogout = () => {
    setToken(null);
    setRole(null);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <div>
        <h1>Медична Клініка</h1>
        <Navbar role={role} onLogout={handleLogout} isAuthenticated={!!token} />

        {!token ? (
          <>
            <Register />
            <Login setToken={setToken} />
          </>
        ) : null}

        <Routes>
          <Route path="/services" element={<ServicesPage />} />
          <Route path="/appointments" element={<AppointmentPage />} />
          <Route path="/services/:id" element={<ServiceDetailsPage />} />
          <Route path="/doctors" element={<DoctorListPage />} />
          <Route path="/doctors/:doctorId" element={<DoctorProfilePage />} />
          <Route path="/doctors" element={<DoctorsPage />} />

          {role === 'doctor' && (
            <Route path="/doctor/profile/create" element={<CreateDoctorProfilePage />} />
          )}

          {role === 'admin' && (
            <>
              <Route path="/admin/services" element={<AdminServicesPage />} />
              <Route path="/admin/categories" element={<AdminCategoriesPage />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
