import React from 'react';
import { Link } from 'react-router-dom';

interface Props {
  role: 'admin' | 'doctor' | 'patient' | null;
  onLogout: () => void;
  isAuthenticated: boolean;
  firstName?: string;
  lastName?: string;
}

const Navbar: React.FC<Props> = ({ role, onLogout, isAuthenticated, firstName, lastName }) => {
  return (
    <nav style={{ marginBottom: '1rem' }}>
      <Link to="/services">Послуги</Link> |{' '}
      <Link to="/doctors">Лікарі</Link>

      {role === 'doctor' && (
        <>
          {' '}| <Link to="/doctor/profile/create">Мій профіль</Link>
        </>
      )}

      {role === 'admin' && (
        <>
          {' '}| <Link to="/admin/services">Адмін: Послуги</Link>
          {' '}| <Link to="/admin/categories">Адмін: Категорії</Link>
        </>
      )}

      {isAuthenticated ? (
        <>
          {' '}| <span>{firstName} {lastName}</span>
          {' '}| <button onClick={onLogout}>Вийти</button>
        </>
      ) : (
        <>
          {' '}| <Link to="/register">Реєстрація</Link>
          {' '}| <Link to="/login">Вхід</Link>
        </>
      )}
    </nav>
  );
};

export default Navbar;
