import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllServices } from '../../api/serviceApi';
import { getAllDoctors } from '../../api/doctorApi';
import { Link } from 'react-router-dom';

interface Service {
  id: number;
  title: string;
  price: number;
  imagePath: string;
  description?: string;
}

interface Doctor {
  id: number;
  firstName: string;
  lastName: string;
  photoUrl: string;
  profile: {
    specialization: string;
    bio: string;
    price: number;
    rating: number | null;
  };
}

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [services, setServices] = useState<Service[]>([]);
  const [doctors, setDoctors] = useState<Doctor[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const servicesRes = await getAllServices();
        const doctorsRes = await getAllDoctors();
        setServices(servicesRes.data.slice(0, 3));
        setDoctors(doctorsRes.data.slice(0, 2));
      } catch (error) {
        console.error('Помилка завантаження даних:', error);
      }
    };

    fetchData();
  }, []);

  const handleBookNow = () => {
    navigate('/services');
  };

  return (
    <div className="px-6 py-10 max-w-screen-xl mx-auto">
      {/* Hero */}
      <section className="text-center mb-16">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 text-primary">
          Ваша турбота — наше покликання
        </h1>
        <p className="text-gray-600 text-lg mb-6">
          Запишіться на прийом до досвідчених лікарів за декілька кліків.
        </p>
        <button
          onClick={handleBookNow}
          className="bg-primary hover:bg-primary-dark text-white px-6 py-3 rounded-xl text-lg transition"
        >
          Записатись зараз
        </button>
      </section>

      {/* Popular Services */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-6">Популярні послуги</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {services.map(service => (
            <div
              key={service.id}
              className="bg-white shadow-md border-2 border-primary rounded-2xl p-4 hover:shadow-lg transition"
            >
              <Link to={`/services/${service.id}`} className="block text-center">
                <img
                  src={`http://localhost:5000/${service.imagePath}`}
                  alt={service.title}
                  className="w-full h-[180px] object-cover rounded-xl mb-4 border-2 border-primary"
                />
                <h3 className="text-xl font-bold text-primary mb-2">{service.title}</h3>
                {service.description && (
                  <p className="text-sm text-primary mb-3 line-clamp-3">{service.description}</p>
                )}
                <p className="text-primary font-semibold text-lg">Від: {service.price} грн</p>
              </Link>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Doctors */}
      <section>
        <h2 className="text-2xl font-semibold mb-6">Рекомендовані лікарі</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {doctors.map(doctor => (
            <div
              key={doctor.id}
              className="bg-white border-2 border-primary rounded-xl shadow-md p-5 flex flex-col justify-between"
            >
              <div className="flex items-center gap-4 mb-4">
                <img
                  src={doctor.photoUrl}
                  alt={`${doctor.firstName} ${doctor.lastName}`}
                  className="w-24 h-24 rounded-full border-2 border-primary object-cover"
                />
                <div>
                  <h3 className="text-xl font-bold text-primary">
                    {doctor.firstName} {doctor.lastName}
                  </h3>
                  <p className="text-primary font-medium">{doctor.profile.specialization}</p>
                  <div className="flex gap-1 mt-1">
                    {[...Array(5)].map((_, i) => (
                      <span key={i} className={`text-accent text-lg ${i < (doctor.profile.rating || 0) ? '' : 'opacity-30'}`}>
                        ★
                      </span>
                    ))}
                  </div>
                </div>
              </div>
              <p className="text-sm text-primary mb-4 line-clamp-3">{doctor.profile.bio}</p>
              <div className="flex justify-between items-center mt-auto">
                <p className="text-primary font-semibold text-lg">Від: {doctor.profile.price} грн</p>
                <Link
                  to={`/doctors/${doctor.id}`}
                  className="bg-primary text-background font-bold text-sm px-4 py-2 rounded-lg hover:bg-blue-800 transition"
                >
                  Записатися
                </Link>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
