import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getDoctorProfile } from '../../api/doctorApi';
import EditDoctorProfileForm from '../../components/doctors/EditDoctorProfileForm';
import BookAppointmentPage from '../appointment/BookAppointmentPage';
import Modal from '../../components/Modal';
import ReviewList from '../../components/review/ReviewList';

const DoctorProfilePage = () => {
  const { doctorId } = useParams<{ doctorId: string }>();
  const [doctor, setDoctor] = useState<any>(null);
  const [isOwnProfile, setIsOwnProfile] = useState(false);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const loadDoctorProfile = async () => {
      if (!doctorId || isNaN(Number(doctorId))) {
        console.error('Invalid doctorId:', doctorId);
        return;
      }

      try {
        const { data } = await getDoctorProfile(Number(doctorId));
        setDoctor(data);

        // Перевірка чи авторизований користувач — це власник профілю
        const tokenPayload = JSON.parse(atob(localStorage.getItem('token')!.split('.')[1]));
        if (tokenPayload.id === Number(doctorId)) {
          setIsOwnProfile(true);
        }
      } catch (error) {
        console.error('Error fetching doctor profile:', error);
      }
    };

    loadDoctorProfile();
  }, [doctorId]);

  if (!doctor) return <div>Loading...</div>;

  return (
  <div className="max-w-[900px] mx-auto p-6 bg-white rounded-xl shadow-lg border border-primary">
    <h1 className="text-primary text-center text-3xl 2xl:text-4xl font-bold mb-8">
      Профіль лікаря
    </h1>

    <div className="flex flex-col md:flex-row gap-8 items-center">
      {/* Фото лікаря */}
      <div className="flex-shrink-0">
        {doctor.photoUrl ? (
          <img
            src={doctor.photoUrl}
            alt={`Фото ${doctor.firstName} ${doctor.lastName}`}
            className="w-48 h-48 md:w-56 md:h-56 rounded-full border-4 border-primary object-cover shadow-md"
          />
        ) : (
          <div className="w-48 h-48 md:w-56 md:h-56 rounded-full bg-gray-200 flex items-center justify-center text-gray-400 text-xl font-semibold">
            Немає фото
          </div>
        )}
      </div>

      {/* Інформація про лікаря */}
      <div className="flex-1 space-y-4 text-primary font-inter">
        <h2 className="text-2xl 2xl:text-3xl font-semibold">
          Dr. {doctor.firstName} {doctor.lastName}
        </h2>

        <p>
          <span className="font-semibold">Email:</span> {doctor.email}
        </p>

        <p>
          <span className="font-semibold">Телефон:</span> {doctor.phone}
        </p>

        <p>
          <span className="font-semibold">Освіта:</span> {doctor.profile?.education}
        </p>

        <p>
          <span className="font-semibold">Спеціалізація:</span> {doctor.profile?.specialization}
        </p>
        
        <p>
          <span className="font-semibold">Досвід:</span> {doctor.profile?.experience}
        </p>


        <p className="text-justify italic leading-relaxed">
          {doctor.profile?.bio}
        <p>
          <span className="font-semibold">Від:</span> {doctor.profile?.price} грн
        </p>
        </p>
      </div>
    </div>

    {/* Кнопка бронювання */}
    <div className="mt-8 flex justify-center">
      <button
        onClick={() => setShowModal(true)}
        className="bg-primary hover:bg-blue-800 text-background font-roboto font-bold text-lg 2xl:text-xl rounded-lg px-8 py-3 transition w-full max-w-[250px]"
      >
        Забронювати
      </button>
    </div>

    {/* Форма редагування для власного профілю */}
    {isOwnProfile && (
      <div className="mt-10 border-t border-primary pt-6">
        <EditDoctorProfileForm userId={Number(doctorId)} />
      </div>
    )}

    {/* Модалка для бронювання */}
    {showModal && (
      <Modal onClose={() => setShowModal(false)}>
        <BookAppointmentPage doctorId={Number(doctorId)} />
      </Modal>
    )}

    {/* Відгуки */}
    <div className="mt-12">
      <ReviewList serviceId={Number(doctorId)} />
    </div>
  </div>

  );
};

export default DoctorProfilePage;
