import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getUserProfile } from '../../api/userApi';
import { Link } from 'react-router-dom';
import AppointmentsListForm from '../../components/services/AppointmentsListForm';

const UserProfilePage = () => {
  const { id } = useParams<{ id: string }>();
  const [user, setUser] = useState<any>(null);
  const [isOwnProfile, setIsOwnProfile] = useState(false);

  useEffect(() => {
    const loadUserProfile = async () => {
      if (!id || isNaN(Number(id))) {
        console.error('Invalid id:', id);
        return;
      }

      try {
        const { data } = await getUserProfile(Number(id));
        setUser(data);

        const token = localStorage.getItem('token');
        if (token) {
          const tokenPayload = JSON.parse(atob(token.split('.')[1]));
          if (tokenPayload.id === Number(id)) {
            setIsOwnProfile(true);
          }
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    loadUserProfile();
  }, [id]);

  if (!user) return <div>Loading...</div>;

  return (
    <div>
      <div className="max-w-xl mx-auto bg-white shadow-md rounded-lg p-6 mt-10">
        <div className="flex items-center space-x-6 mb-6">
          {user.photoUrl ? (
            <img
              src={user.photoUrl.startsWith('http') ? user.photoUrl : `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/${user.photoUrl}`}
              alt={`${user.firstName} ${user.lastName}`}
              className="w-28 h-28 object-cover rounded-full border-2 border-blue-500"
            />
          ) : (
            <div className="w-28 h-28 flex items-center justify-center bg-gray-200 rounded-full text-gray-500 text-xl font-semibold">
              Немає фото
            </div>
          )}
          <div>
            <h2 className="text-2xl font-bold">{user.firstName} {user.lastName}</h2>
          </div>
        </div>

        <div className="mb-4 space-y-2 text-gray-700">
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Телефон:</strong> {user.phone}</p>
        </div>

        {isOwnProfile && (
          <Link
            to={`/user/profile/edit/${user.id}`}
            className="inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Змінити профіль
          </Link>
        )}

      </div>
      <div>
        <AppointmentsListForm />
      </div>
    </div>
  );
};

export default UserProfilePage;
