import React, { useState, useEffect } from 'react';
import { getAllServices } from '../api/serviceApi';
import { createAppointment } from '../api/appointmentApi';
import axios from 'axios';

interface Service {
  id: number;
  title: string;
}

interface Doctor {
  id: number;
  name: string;
}

const BookAppointmentPage: React.FC<{ serviceId?: number }> = ({ serviceId: prefilledServiceId }) => {
  const [doctorId, setDoctorId] = useState('');
  const [serviceId, setServiceId] = useState(prefilledServiceId?.toString() || '');
  const [date, setDate] = useState('');
  const [services, setServices] = useState<Service[]>([]);
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState<string | null>(localStorage.getItem('token') || '');

  // Отримання послуг
  useEffect(() => {
    getAllServices()
      .then(response => setServices(response.data))
      .catch(() => setMessage('Failed to load services.'));
  }, []);

  // Отримання лікарів
  useEffect(() => {
    if (!token) return;
  
    axios.get('http://localhost:5000/api/users/doctors', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => setDoctors(res.data))
      .catch(() => setMessage('Failed to load doctors.'));
  }, [token]);
  

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!token) {
      setMessage('Please log in to book an appointment.');
      return;
    }

    try {
      await createAppointment({ doctorId: Number(doctorId), serviceId: Number(serviceId), date }, token);
      setMessage('Appointment created successfully!');
      setDoctorId('');
      setServiceId(prefilledServiceId?.toString() || '');
      setDate('');
    } catch (err) {
      setMessage('Failed to create appointment.');
    }
  };

  return (
    <div>
      <h1>Book an Appointment</h1>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Doctor:</label>
          <select value={doctorId} onChange={(e) => setDoctorId(e.target.value)} required>
            <option value="">Select a doctor</option>
            {doctors.map((doctor) => (
              <option key={doctor.id} value={doctor.id}>
                {doctor.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Service:</label>
          <select value={serviceId} onChange={(e) => setServiceId(e.target.value)} required>
            <option value="">Select a service</option>
            {services.map((service) => (
              <option key={service.id} value={service.id}>
                {service.title}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Date:</label>
          <input
            type="datetime-local"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
};

export default BookAppointmentPage;
