import React, { useState } from 'react';
import { createDoctorProfile } from '../../api/doctorApi';

const CreateDoctorProfileForm = () => {
  const [form, setForm] = useState({
    education: '',
    experience: '',
    bio: '',
    specialization: '',
    price: '',  // зберігаємо як string, щоб пов’язати з input[type=number]
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Будь ласка, увійдіть в систему');
      return;
    }

    try {
      await createDoctorProfile(
        {
          education: form.education,
          experience: form.experience,
          bio: form.bio,
          specialization: form.specialization,
          price: Number(form.price),  // перетворюємо в number
        },
        token
      );
      alert('Профіль створено успішно!');
    } catch (err) {
      console.error('Помилка створення профілю:', err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="education" placeholder="Освіта" value={form.education} onChange={handleChange} required />
      <input name="experience" placeholder="Досвід" value={form.experience} onChange={handleChange} required />
      <textarea name="bio" placeholder="Про себе" value={form.bio} onChange={handleChange} required />
      <input name="specialization" placeholder="Спеціалізація" value={form.specialization} onChange={handleChange} required />
      <input
        name="price"
        placeholder="Ціна"
        type="number"
        min="0"
        value={form.price}
        onChange={handleChange}
        required
      />
      <button type="submit">Створити профіль</button>
    </form>
  );
};

export default CreateDoctorProfileForm;
