import React, { useState, useEffect } from 'react';
import { createAppointment } from '../../api/appointmentApi';
import { getAllServices } from '../../api/serviceApi';

interface Service {
  id: number;
  title: string;
}

interface AppointmentFormProps {
  preselectedServiceId?: number;
}

const AppointmentForm: React.FC<AppointmentFormProps> = ({ preselectedServiceId }) => {
  const [doctorId, setDoctorId] = useState('');
  const [serviceId, setServiceId] = useState(preselectedServiceId?.toString() || '');
  const [date, setDate] = useState('');
  const [services, setServices] = useState<Service[]>([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    getAllServices()
      .then(data => setServices(data.data))
      .catch(() => setMessage('Failed to load services.'));
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('You must be logged in to book an appointment.');
      return;
    }

    try {
      await createAppointment({
        doctorId: Number(doctorId),
        serviceId: Number(serviceId),
        date
      }, token);

      setMessage('Appointment created successfully!');
      setDoctorId('');
      setServiceId('');
      setDate('');
    } catch (err) {
      setMessage('Failed to create appointment.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Book an Appointment</h3>

      <div>
        <label>Doctor ID:</label>
        <input
          type="number"
          value={doctorId}
          onChange={(e) => setDoctorId(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Service:</label>
        <select value={serviceId} onChange={(e) => setServiceId(e.target.value)} required>
          <option value="">Select a service</option>
          {services.map((service) => (
            <option key={service.id} value={service.id}>
              {service.title}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label>Date:</label>
        <input
          type="datetime-local"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </div>

      <button type="submit">Book</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default AppointmentForm;
