import React, { useState, useEffect } from 'react';
import { getAllCategories, updateCategory, deleteCategory } from '../../api/categoryApi';
import CategoryForm from '../../components/categories/CategoryForm';
import EditCategoryForm from '../../components/categories/EditCategoryForm';

interface Category {
  id: number;
  name: string;
}

const AdminCategoriesPage: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [editingCategoryId, setEditingCategoryId] = useState<number | null>(null);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) setToken(storedToken);

    loadCategories();
  }, []);

  const loadCategories = () => {
    getAllCategories()
      .then(res => setCategories(res.data))
      .catch(() => setMessage('Не вдалося завантажити категорії.'));
  };

  const handleUpdate = (id: number, newName: string) => {
    if (!token) return;
    updateCategory(id, newName, token)
      .then(() => {
        setMessage('Категорію оновлено.');
        setEditingCategoryId(null);
        loadCategories();
      })
      .catch(() => setMessage('Помилка при оновленні.'));
  };

  const handleDelete = (id: number) => {
    if (!token) return;
    if (window.confirm('Ви впевнені, що хочете видалити цю категорію?')) {
      deleteCategory(id, token)
        .then(() => {
          setMessage('Категорію видалено.');
          loadCategories();
        })
        .catch(() => setMessage('Помилка при видаленні.'));
    }
  };

  return (
    <div>
      <h1>Адмін-панель категорій</h1>
      {message && <p>{message}</p>}

      <h3>Список категорій</h3>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            {editingCategoryId === category.id ? (
              <EditCategoryForm
                id={category.id}
                currentName={category.name}
                onSave={(newName) => handleUpdate(category.id, newName)}
                onCancel={() => setEditingCategoryId(null)}
              />
            ) : (
              <>
                {category.name}
                {token && (
                  <>
                    <button onClick={() => setEditingCategoryId(category.id)}>Редагувати</button>
                    <button onClick={() => handleDelete(category.id)}>Видалити</button>
                  </>
                )}
              </>
            )}
          </li>
        ))}
      </ul>

      <h3>Створити нову категорію</h3>
      {token ? (
        <CategoryForm token={token} />
      ) : (
        <p>Увійдіть у систему для створення категорій.</p>
      )}
    </div>
  );
};

export default AdminCategoriesPage;
