import React, { useState, useEffect } from 'react';
import { getAllServices, hideService } from '../api/serviceApi';
import ServiceForm from '../components/services/ServiceForm';

interface Service {
  id: number;
  title: string;
  description: string;
  price: number;
}

const AdminServicesPage: React.FC = () => {
  const [services, setServices] = useState<Service[]>([]);
  const [message, setMessage] = useState('');
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
    }
    getAllServices()
      .then(response => setServices(response.data)) // отримуємо сервіси
      .catch(() => setMessage('Failed to load services.'));
  }, []);

  const handleHide = async (id: number) => {
    try {
      await hideService(id, token!);
      setServices(prevServices => prevServices.filter(service => service.id !== id)); // Видаляємо сервіс зі списку
      alert('Сервіс приховано!');
    } catch (error) {
      alert('Помилка при приховуванні сервісу');
    }
  };

  return (
    <div>
      <h1>Admin Services Page</h1>

      {/* Виведення повідомлення про помилки */}
      {message && <p>{message}</p>}

      <h3>Services List</h3>
      <ul>
        {services.map(service => (
          <li key={service.id}>
            {service.title} - {service.description} - ${service.price}
            {/* Кнопка для приховування сервісу */}
            <button onClick={() => handleHide(service.id)}>Hide</button>
          </li>
        ))}
      </ul>

      <h3>Create a New Service</h3>
      {token ? (
        <ServiceForm token={token} />
      ) : (
        <p>Please log in to create services.</p>
      )}
    </div>
  );
};

export default AdminServicesPage;
