// File: src/api/serviceApi.ts
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/services';

export const getAllServices = () => axios.get(API_URL);
export const createService = (data: FormData, token: string) =>
    axios.post(`${API_URL}/create`, data, {
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'multipart/form-data', // Вказуємо тип контенту для файлів
      },
    });

export const updateService = (id: number, data: any, token: string) =>
  axios.put(`${API_URL}/${id}`, data, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const hideService = (id: number, token: string) =>
  axios.patch(`${API_URL}/${id}/hide`, {}, {
    headers: { Authorization: `Bearer ${token}` },
  });

  export const assignDoctorToService = (serviceId: number, doctorId: number, token: string) => {
    return axios.post(
      `http://localhost:5000/api/services/${serviceId}/doctors`,
      { doctorId },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
  };
  
  export const getAllDoctors = () => {
    return axios.get('http://localhost:5000/api/doctors');
  };
