import React, { useState, useEffect } from 'react';
import { getAllDoctors } from '../../api/doctorApi';

export interface ServiceFormData {
  title: string;
  description: string;
  price: number;
  categoryId?: number;
  image: File | null;
  doctors?: { id: number; firstName: string; lastName: string }[];
}

interface Props {
  token: string;
  initialData?: ServiceFormData;
  onSubmit: (data: ServiceFormData) => void;
}

interface Category {
  id: number;
  name: string;
}

interface User {
  id: number;
  firstName: string;
  lastName: string;
}

const ServiceForm: React.FC<Props> = ({ token, initialData, onSubmit }) => {
  const [title, setTitle] = useState(initialData?.title || '');
  const [description, setDescription] = useState(initialData?.description || '');
  const [price, setPrice] = useState(initialData?.price?.toString() || '');
  const [categoryId, setCategoryId] = useState<number | string>(initialData?.categoryId || '');
  const [image, setImage] = useState<File | null>(null);
  const [categories, setCategories] = useState<Category[]>([]);
  const [doctors, setDoctors] = useState<User[]>([]);
  const [selectedDoctors, setSelectedDoctors] = useState<User[]>(initialData?.doctors || []);

  useEffect(() => {
    fetch('http://localhost:5000/api/categories')
      .then(res => res.json())
      .then(setCategories)
      .catch(console.error);

    getAllDoctors()
      .then(res => setDoctors(res.data))
      .catch(console.error);
  }, []);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setImage(e.target.files[0]);
    }
  };

  const handleDoctorSelect = (doctor: User) => {
    if (!selectedDoctors.some(d => d.id === doctor.id)) {
      setSelectedDoctors(prev => [...prev, doctor]);
    }
  };

  const handleDoctorRemove = (doctorId: number) => {
    setSelectedDoctors(prev => prev.filter(doc => doc.id !== doctorId));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const formValues: ServiceFormData = {
      title,
      description,
      price: Number(price),
      categoryId: categoryId ? Number(categoryId) : undefined,
      image,
      doctors: selectedDoctors,
    };

    onSubmit(formValues);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        value={title}
        onChange={e => setTitle(e.target.value)}
        placeholder="Назва"
        required
      />
      <textarea
        value={description}
        onChange={e => setDescription(e.target.value)}
        placeholder="Опис"
        required
      />
      <input
        value={price}
        onChange={e => setPrice(e.target.value)}
        placeholder="Ціна"
        type="number"
        required
      />

      <select value={categoryId} onChange={e => setCategoryId(e.target.value)}>
        <option value="">Виберіть категорію</option>
        {categories.map(category => (
          <option key={category.id} value={category.id}>
            {category.name}
          </option>
        ))}
      </select>

      <input type="file" onChange={handleImageChange} />

      <div>
        <h3>Виберіть лікарів</h3>
        <select
          onChange={(e) => {
            const selected = doctors.find(doc => doc.id === Number(e.target.value));
            if (selected) handleDoctorSelect(selected);
          }}
          defaultValue=""
        >
          <option value="">Виберіть лікаря</option>
          {doctors.map(doc => (
            <option key={doc.id} value={doc.id}>
              {doc.firstName} {doc.lastName}
            </option>
          ))}
        </select>

        <div>
          <h4>Вибрані лікарі:</h4>
          <ul>
            {selectedDoctors.map(doc => (
              <li key={doc.id}>
                {doc.firstName} {doc.lastName}
                <button type="button" onClick={() => handleDoctorRemove(doc.id)}>Видалити</button>
              </li>
            ))}
          </ul>
        </div>
      </div>

      <button type="submit">Зберегти</button>
    </form>
  );
};

export default ServiceForm;
