// src/api/doctorApi.ts
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/users/';

// Отримати профіль лікаря за його ID
export const getDoctorProfile = (doctorId: number) => {
  return axios.get(`${API_URL}doctor/profile${doctorId}`);
};

// Створити профіль лікаря

export const createDoctorProfile = async (
  profileData: {
    education: string;
    experience: string;
    bio: string;
    photoUrl?: string;
  },
  token: string
) => {
  const userId = localStorage.getItem('userId');
  if (!userId) throw new Error('User ID not found');

  return axios.post(`${API_URL}doctor/profile/create/${userId}`, profileData, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};





// Оновити профіль лікаря
export const updateDoctorProfile = (userId: number, profileData: {
  education?: string;
  experience?: string;
  bio?: string;
  photoUrl?: string;
}) => {
  return axios.put(`${API_URL}doctor/profile/${userId}`, profileData);
};

// Отримати список всіх лікарів
export const getAllDoctors = () => {
  return axios.get(`${API_URL}doctors`);
};
