// src/pages/ServiceDetailsPage.tsx
import { useParams } from 'react-router-dom';
import AppointmentForm from '../components/services/AppointmentForm';

import { useEffect, useState } from 'react';
import axios from 'axios';

const ServiceDetailsPage = () => {
  const { id } = useParams<{ id: string }>();
  const [service, setService] = useState<any>(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/services/${id}`)
      .then(res => setService(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!service) return <p>Loading...</p>;

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <img
        src={`http://localhost:5000/${service.imagePath}`}
        alt={service.title}
        style={{ width: '100%', height: '300px', objectFit: 'cover' }}
      />
      <h1>{service.title}</h1>
      <p>{service.description}</p>
      <h3>Ціна: ${service.price}</h3>

      {/* Кнопка для бронювання */}
      <button onClick={() => alert('Функція бронювання ще не реалізована')}>
        Забронювати
      </button>
        <h3>Бронювання</h3>
        <AppointmentForm preselectedServiceId={service.id} />
    </div>
  );
};

export default ServiceDetailsPage;
