import React, { useState } from 'react';
import { updateAppointment } from '../../api/appointmentApi';

interface Appointment {
  id: number;
  date: string;
  status: 'pending' | 'confirmed' | 'finished' | 'cancelled';
  doctor: { firstName: string; lastName: string };
  patient: { firstName: string; lastName: string };
}

interface UpdateAppointmentProps {
  appointment: Appointment;
}

const UpdateAppointmentForm: React.FC<UpdateAppointmentProps> = ({ appointment }) => {
  const [newDate, setNewDate] = useState(appointment.date);
  const [newStatus, setNewStatus] = useState(appointment.status);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');
  const userRole = localStorage.getItem('role'); // Отримання ролі користувача

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!token) return setMessage('Необхідно увійти в систему.');
    const updatedAppointment = {
      date: newDate,
      status: newStatus as 'pending' | 'confirmed' | 'finished' | 'cancelled',  // явно вказуємо тип
    };
    try {
      await updateAppointment(appointment.id, updatedAppointment, token);
      setMessage('Бронювання успішно оновлено.');
    } catch (error) {
      setMessage('Не вдалося оновити бронювання.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Оновити бронювання</h2>

      <div>
        <label>Дата:</label>
        <input
          type="datetime-local"
          value={newDate}
          onChange={(e) => setNewDate(e.target.value)}
        />
      </div>

      {appointment.status !== 'finished' && (userRole === 'doctor' || userRole === 'admin') && (
        <div>
          <label>Статус:</label>
          <select
            value={newStatus}
            onChange={(e) => setNewStatus(e.target.value as 'pending' | 'confirmed' | 'finished' | 'cancelled')}
          >
            <option value="pending">Очікується</option>
            <option value="confirmed">Підтверджено</option>
            <option value="finished">Завершено</option>
            <option value="cancelled">Скасовано</option>
          </select>
        </div>
      )}

      <button type="submit">Оновити</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default UpdateAppointmentForm;
