import React, { useEffect, useState } from 'react';
import { getDoctorReviews, getServiceReviews, deleteReview } from '../../api/reviewApi';

type Review = {
  id: number;
  rating: number;
  comment: string;
  createdAt: string;
  appointment: {
    Service?: { title: string };
    doctor?: { firstName: string; lastName: string };
  };
  author?: {
    firstName: string;
    lastName: string;
  };
};

type Props = {
  doctorId?: number;
  serviceId?: number;
  token?: string;
  showDelete?: boolean;
};

const ReviewList: React.FC<Props> = ({ doctorId, serviceId, token, showDelete = false }) => {
  const [reviews, setReviews] = useState<Review[]>([]);

  useEffect(() => {
    const fetch = async () => {
      const data = doctorId
        ? await getDoctorReviews(doctorId)
        : serviceId
        ? await getServiceReviews(serviceId)
        : [];
      setReviews(data);
      console.log(data);
    };
    fetch();
  }, [doctorId, serviceId]);

  const handleDelete = async (id: number) => {
    if (!token) return;
    await deleteReview(id, token);
    setReviews(reviews.filter(r => r.id !== id));
  };

  return (
    <div>
      <h2>Відгуки:</h2>
      {reviews.length === 0 ? (
        <p>Відгуків немає</p>
      ) : (
        reviews.map(r => (
          
            <div className="bg-primary text-background rounded-xl p-4 lg:p-5 mt-4">
              <div className="flex justify-between items-center">
                <div className="font-roboto font-bold text-base lg:text-lg xl:text-xl tracking-[1px]">
                  Андрій Ковальчук
                </div>
                <div className="flex items-center gap-1">
                  {[...Array(5)].map((_, i) => (
                    <span key={i} className="text-accent text-[20px] lg:text-[22px] 2xl:text-[25px]">★</span>
                  ))}
                </div>
              </div>
              <p className="font-playfair italic font-light text-[10px] md:text-sm 2xl:text-base mt-2">
                Відгук від користувача, який може бути на 2–3 рядки. Можна скоротити або зробити кнопку "читати більше".
              </p>
            </div>
        ))
      )}
    </div>
  );
};

export default ReviewList;
