import axios from 'axios';
import { Service, getAllServices, getDoctorsForService } from '../api/serviceApi';

export const getAllDoctors = () => {
  return axios.get('http://localhost:5000/api/users/doctors');
};

const getAllServicesWithDoctors = async () => {
  try {
    const response = await getAllServices();
    const servicesWithDoctors = await Promise.all(
      response.data.map(async (service: Service) => {
        const doctorsResponse = await getDoctorsForService(service.id);
        const doctorsData = doctorsResponse.data;
        return {
          ...service,
          doctors: doctorsData,
        };
      })
    );
    setServices(servicesWithDoctors);
  } catch (error) {
    setMessage('Не вдалося завантажити сервіси та лікарів.');
  }
};
