import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  getServiceById,
  updateService,
  assignDoctorToService,
  removeDoctorFromService,
} from '../api/serviceApi';
import { getAllDoctors } from '../api/doctorApi';
import { getAllCategories } from '../api/categoryApi';

type Doctor = {
  id: number;
  firstName: string;
  lastName: string;
};

type Category = {
  id: number;
  name: string;
};

const EditServicePage = () => {
  const token = localStorage.getItem('token') || '';
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [categoryId, setCategoryId] = useState<number | string>('');
  const [image, setImage] = useState<File | null>(null);
  const [imagePath, setImagePath] = useState('');
  const [categories, setCategories] = useState<Category[]>([]);
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [selectedDoctors, setSelectedDoctors] = useState<Doctor[]>([]);
  const [isHidden, setIsHidden] = useState(false);

  useEffect(() => {
    if (!id) return;

    const fetchData = async () => {
      try {
        const data = await getServiceById(id);
        setTitle(data.title);
        setDescription(data.description);
        setPrice(data.price.toString());
        setCategoryId(data.categoryId);
        setImagePath(data.imagePath || '');
        setSelectedDoctors(data.doctors || []);
        setIsHidden(data.isHidden);
      } catch (error) {
        console.error('Error fetching service:', error);
      }

      try {
        const res = await getAllCategories();
        setCategories(res.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }

      try {
        const res = await getAllDoctors();
        setDoctors(res.data);
      } catch (error) {
        console.error('Error fetching doctors:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleDoctorSelect = async (doctorId: number) => {
    if (!id || !token) return;

    try {
      await assignDoctorToService(Number(id), doctorId, token);
      const doctor = doctors.find((d) => d.id === doctorId);
      if (doctor) {
        setSelectedDoctors((prev) => [...prev, doctor]);
      }
    } catch (error) {
      console.error('Error assigning doctor:', error);
    }
  };

  const handleDoctorRemove = async (doctorId: number) => {
    if (!id || !token) return;

    try {
      await removeDoctorFromService(Number(id), doctorId, token);
      setSelectedDoctors((prev) => prev.filter((d) => d.id !== doctorId));
    } catch (error) {
      console.error('Error removing doctor:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!id || !token) return;

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('price', price);
    formData.append('categoryId', categoryId.toString());
    formData.append('isHidden', isHidden.toString());
    if (image) formData.append('image', image);

    try {
      await updateService(id, formData, token);
      alert('Сервіс оновлено!');
      navigate('/admin/services');
    } catch (error) {
      alert('Помилка при оновленні сервісу');
      console.error('Update error:', error);
    }
  };

  return (
    <div>
      <h1>Редагувати сервіс</h1>
      <form onSubmit={handleSubmit}>
        <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Назва" required />
        <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Опис" required />
        <input value={price} onChange={e => setPrice(e.target.value)} placeholder="Ціна" type="number" required />

        <select value={categoryId} onChange={e => setCategoryId(e.target.value)} required>
          <option value="">Виберіть категорію</option>
          {categories.map(category => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </select>

        <div>
          <label>Статус сервісу:</label>
          <label>
            <input type="radio" name="isHidden" value="false" checked={!isHidden} onChange={() => setIsHidden(false)} />
            Видимий
          </label>
          <label>
            <input type="radio" name="isHidden" value="true" checked={isHidden} onChange={() => setIsHidden(true)} />
            Прихований
          </label>
        </div>

        {imagePath && (
          <div>
            <h3>Зображення сервісу:</h3>
            <img src={`http://localhost:5000/${imagePath}`} alt="Service" style={{ width: '200px' }} />
          </div>
        )}

        <input type="file" onChange={handleImageChange} />

        <div>
          <h3>Виберіть лікаря</h3>
          <select onChange={(e) => handleDoctorSelect(Number(e.target.value))} defaultValue="">
            <option value="">Виберіть лікаря</option>
            {doctors.map(doctor => (
              <option key={doctor.id} value={doctor.id}>
                {doctor.firstName} {doctor.lastName}
              </option>
            ))}
          </select>

          <h4>Прив’язані лікарі:</h4>
          <ul>
            {selectedDoctors.map((doctor) => (
              <li key={doctor.id}>
                {doctor.firstName} {doctor.lastName}
                <button type="button" onClick={() => handleDoctorRemove(doctor.id)}>Видалити</button>
              </li>
            ))}
          </ul>
        </div>

        <button type="submit">Оновити</button>
      </form>
    </div>
  );
};

export default EditServicePage;
