import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // для отримання параметрів URL
import { getAllServices } from '../api/serviceApi';
import { createAppointment, getBookedSlots } from '../api/appointmentApi';
import axios from 'axios';
import AppointmentForm from '../components/services/AppointmentForm';

interface Doctor {
  id: number;
  firstName: string;
  lastName: string;
}

const BookAppointmentPage: React.FC = () => {
  const { serviceId } = useParams<{ id: string }>(); // Отримуємо serviceId з URL
  const [doctorId, setDoctorId] = useState('');
  const [appointmentData, setAppointmentData] = useState({ date: '', time: '' });
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [bookedSlots, setBookedSlots] = useState<string[]>([]);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!serviceId) {
      setMessage('Не вдалося отримати послугу.');
      return;
    }

    // В цьому випадку можна не завантажувати список послуг, оскільки serviceId вже є в URL
    setMessage(''); // Очистити повідомлення, якщо serviceId отримано
  }, [serviceId]);

  useEffect(() => {
    if (!token) return;

    axios.get('http://localhost:5000/api/users/doctors', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setDoctors(res.data))
      .catch(() => setMessage('Не вдалося завантажити лікарів.'));
  }, [token]);

  useEffect(() => {
    if (doctorId && appointmentData.date && token) {
      getBookedSlots(Number(doctorId), appointmentData.date, token)
        .then((res) => {
          setBookedSlots(res.data); // має бути список рядків типу "09:00"
        })
        .catch(() => setBookedSlots([]));
    } else {
      setBookedSlots([]);
    }
  }, [doctorId, appointmentData.date, token]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!token) {
      setMessage('Будь ласка, увійдіть для запису.');
      return;
    }

    const { date, time } = appointmentData;
    if (!date || !time) {
      setMessage('Оберіть дату та час.');
      return;
    }

    try {
      const datetime = `${date}T${time}:00`;
      await createAppointment(
        {
          doctorId: Number(doctorId),
          serviceId: Number(serviceId), // Використовуємо serviceId з URL
          date: datetime,
        },
        token
      );
      setMessage('Запис успішно створено!');
      setDoctorId('');
      setAppointmentData({ date: '', time: '' });
    } catch (err) {
      setMessage('Помилка при створенні запису.');
    }
  };

  return (
    <div>
      <h1>Запис на прийом</h1>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Лікар:</label>
          <select value={doctorId} onChange={(e) => setDoctorId(e.target.value)} required>
            <option value="">Оберіть лікаря</option>
            {doctors.map((doctor) => (
              <option key={doctor.id} value={doctor.id}>
                {doctor.firstName} {doctor.lastName}
              </option>
            ))}
          </select>
        </div>

        {/* Вилучаємо поле вибору послуги */}
        {/* <div>
          <label>Послуга:</label>
          <select value={serviceId} onChange={(e) => setServiceId(e.target.value)} required>
            <option value="">Оберіть послугу</option>
            {services.map((service) => (
              <option key={service.id} value={service.id}>
                {service.title}
              </option>
            ))}
          </select>
        </div> */}

        <AppointmentForm
          value={appointmentData}
          onChange={setAppointmentData}
          doctorId={Number(doctorId)}
          token={token || ''}  
        />

        <button type="submit">Записатись</button>
      </form>
    </div>
  );
};

export default BookAppointmentPage;
