//src/components/services/ServiceForm.tsx
import { useState } from 'react';
import { createService } from '../../api/serviceApi';

const ServiceForm = ({ token }: { token: string }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await createService({ title, description, price: parseFloat(price) }, token);
      alert('Сервіс створено!');
    } catch (error) {
      alert('Помилка при створенні сервісу');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Назва" />
      <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Опис" />
      <input value={price} onChange={e => setPrice(e.target.value)} placeholder="Ціна" type="number" />
      <button type="submit">Створити</button>
    </form>
  );
};

export default ServiceForm;
