import React from 'react';
import { Link } from 'react-router-dom';

interface Props {
  role: 'admin' | 'doctor' | 'patient' | null;
  onLogout: () => void;
  isAuthenticated: boolean;
}

const Navbar: React.FC<Props> = ({ role, onLogout, isAuthenticated }) => {
  return (
    <nav style={{ marginBottom: '1rem' }}>
      <Link to="/services">Послуги</Link> |{' '}
      <Link to="/appointments">Бронювання</Link> |{' '}
      <Link to="/doctors">Лікарі</Link>

      {role === 'doctor' && (
        <>
          {' '}| <Link to="/doctor/profile/create">Мій профіль</Link>
        </>
      )}

      {role === 'admin' && (
        <>
          {' '}| <Link to="/admin/services">Адмін: Послуги</Link>{' '}
          | <Link to="/admin/categories">Адмін: Категорії</Link>
        </>
      )}

      {isAuthenticated ? (
        <>
          {' '}| <button onClick={onLogout}>Вийти</button>
        </>
      ) : null}
    </nav>
  );
};

export default Navbar;
