import React from 'react';
import { useParams } from 'react-router-dom';
import ReviewForm from '../components/review/ReviewForm'; // Компонент з формою

interface ReviewFormPageProps {
  userId: number | null;
}

const ReviewFormPage: React.FC<ReviewFormPageProps> = ({ userId }) =>  {
  const appointmentId = 123; // або витягнути з useParams()

  if (!userId) {
    return <p>Будь ласка, увійдіть, щоб залишити відгук.</p>;
  }

  return <ReviewForm appointmentId={appointmentId} id={userId} />;
};

export default ReviewFormPage;
