import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getServiceById, updateService, assignDoctorToService, removeDoctorFromService } from '../api/serviceApi';
import { getAllDoctors } from '../api/doctorApi';
import { getAllCategories } from '../api/categoryApi';

type Doctor = {
  id: number;
  firstName: string;
  lastName: string;
};

type Category = {
  id: number;
  name: string;
};

const EditServicePage = () => {
  const token = localStorage.getItem('token') || '';
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [categoryId, setCategoryId] = useState<number | string>('');
  const [image, setImage] = useState<File | null>(null);
  const [imagePath, setImagePath] = useState<string>(''); 
  const [categories, setCategories] = useState<Category[]>([]);
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [selectedDoctors, setSelectedDoctors] = useState<Doctor[]>([]);
  const [isHidden, setIsHidden] = useState(false);

  useEffect(() => {
    if (!id) return;

    getServiceById(id)
      .then((data) => {
        setTitle(data.title);
        setDescription(data.description);
        setPrice(data.price.toString());
        setCategoryId(data.categoryId);
        setSelectedDoctors(data.doctors || []);
        setImagePath(data.imagePath || '');
        setIsHidden(data.isHidden);
      })
      .catch((error) => console.error('Error fetching service:', error));

    getAllCategories()
      .then((res) => setCategories(res.data))
      .catch((error) => console.error('Error fetching categories:', error));

    getAllDoctors()
      .then((res) => setDoctors(res.data))
      .catch((error) => console.error('Error fetching doctors:', error));
  }, [id]);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleDoctorSelect = (doctorId: number) => {
    const doctor = doctors.find(d => d.id === doctorId);
    if (doctor && !selectedDoctors.some(d => d.id === doctor.id)) {
      setSelectedDoctors(prevDoctors => [...prevDoctors, doctor]);
    }
  };

  const handleDoctorRemove = (doctorId: number) => {
    setSelectedDoctors(prevDoctors => prevDoctors.filter(d => d.id !== doctorId));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!id) return;

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('price', price);
    formData.append('categoryId', categoryId.toString());
    formData.append('isHidden', isHidden.toString());

    if (image) {
      formData.append('image', image);
    }

    selectedDoctors.forEach(doctor => {
      formData.append('doctors[]', doctor.id.toString());
    });

    try {
      await updateService(id, formData, token);

      // Handle adding/removing doctors
      const currentDoctorsIds = selectedDoctors.map(d => d.id);
      const initialDoctorsIds = (data?.doctors || []).map(d => d.id);
      const addedDoctorIds = currentDoctorsIds.filter(id => !initialDoctorsIds.includes(id));
      const removedDoctorIds = initialDoctorsIds.filter(id => !currentDoctorsIds.includes(id));

      // Add new doctors
      for (const doctorId of addedDoctorIds) {
        await assignDoctorToService(Number(id), doctorId, token);
      }
      // Remove doctors
      for (const doctorId of removedDoctorIds) {
        await removeDoctorFromService(Number(id), doctorId, token);
      }

      alert('Сервіс оновлено!');
      navigate('/admin/services');
    } catch (error) {
      alert('Помилка при оновленні сервісу');
      console.error('Update error:', error);
    }
  };

  return (
    <div>
      <h1>Редагувати сервіс</h1>
      <form onSubmit={handleSubmit}>
        <input
          value={title}
          onChange={e => setTitle(e.target.value)}
          placeholder="Назва"
          required
        />
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          placeholder="Опис"
          required
        />
        <input
          value={price}
          onChange={e => setPrice(e.target.value)}
          placeholder="Ціна"
          type="number"
          required
        />
        <select
          value={categoryId}
          onChange={e => setCategoryId(e.target.value)}
          required
        >
          <option value="">Виберіть категорію</option>
          {categories.map(category => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>

        <div>
          <label>Статус сервісу:</label>
          <div>
            <label>
              <input
                type="radio"
                name="isHidden"
                value="false"
                checked={!isHidden}
                onChange={() => setIsHidden(false)}
              />
              Видимий
            </label>
            <label>
              <input
                type="radio"
                name="isHidden"
                value="true"
                checked={isHidden}
                onChange={() => setIsHidden(true)}
              />
              Прихований
            </label>
          </div>
        </div>

        {imagePath && (
          <div>
            <h3>Зображення сервісу:</h3>
            <img src={`http://localhost:5000/${imagePath}`} alt="Service" style={{ width: '200px', height: 'auto' }} />
          </div>
        )}

        <input type="file" onChange={handleImageChange} />

        {/* Select doctors to add */}
        <div>
          <h3>Додати лікарів:</h3>
          <select onChange={(e) => handleDoctorSelect(Number(e.target.value))} required>
            <option value="">Виберіть лікаря</option>
            {doctors.map(doctor => (
              <option key={doctor.id} value={doctor.id}>
                {doctor.firstName} {doctor.lastName}
              </option>
            ))}
          </select>
        </div>

        {/* Display assigned doctors with remove option */}
        <div>
          <h3>Призначені лікарі:</h3>
          {selectedDoctors.length > 0 ? (
            <ul>
              {selectedDoctors.map((doctor) => (
                <li key={doctor.id}>
                  {doctor.firstName} {doctor.lastName} 
                  <button type="button" onClick={() => handleDoctorRemove(doctor.id)}>Видалити</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>Немає призначених лікарів.</p>
          )}
        </div>

        <button type="submit">Оновити</button>
      </form>
    </div>
  );
};

export default EditServicePage;
