import React from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage: React.FC = () => {
  const navigate = useNavigate();

  const handleBookNow = () => {
    navigate('/services'); // або на сторінку з послугами/лікарями
  };

  return (
    <div className="px-6 py-10 max-w-screen-xl mx-auto">
      {/* Hero Section */}
      <section className="text-center mb-16">
        <h1 className="text-4xl md:text-5xl font-bold mb-4 text-primary">
          Ваша турбота — наше покликання
        </h1>
        <p className="text-gray-600 text-lg mb-6">
          Запишіться на прийом до досвідчених лікарів за декілька кліків.
        </p>
        <button
          onClick={handleBookNow}
          className="bg-primary hover:bg-primary-dark text-white px-6 py-3 rounded-xl text-lg transition"
        >
          Записатись зараз
        </button>
      </section>

      {/* Popular Services */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">Популярні послуги</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {/* Тут можна динамічно завантажити сервіси */}
          {['Кардіологія', 'Терапія', 'Дерматологія'].map((title, index) => (
            <div
              key={index}
              className="bg-white shadow-md rounded-2xl p-4 hover:shadow-lg transition"
            >
              <h3 className="text-lg font-medium text-gray-800">{title}</h3>
              <p className="text-sm text-gray-500 mt-1">Короткий опис послуги...</p>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Doctors */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Рекомендовані лікарі</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {/* Тут також можна використовувати API */}
          {[1, 2, 3].map((id) => (
            <div
              key={id}
              className="bg-white shadow-md rounded-2xl p-4 hover:shadow-lg transition"
            >
              <div className="h-24 w-24 bg-gray-200 rounded-full mb-4 mx-auto"></div>
              <h3 className="text-lg font-semibold text-center">Д-р Ім'я Прізвище</h3>
              <p className="text-sm text-gray-500 text-center">Спеціальність</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
