// src/components/services/CreateDoctorProfileForm.tsx
import React, { useState } from 'react';
import { createDoctorProfile } from '../api/doctorApi';
import { parseJwt } from '../utils/jwt';

const CreateDoctorProfileForm: React.FC = () => {
  const [form, setForm] = useState({
    education: '',
    experience: '',
    bio: '',
    photoUrl: '',
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Будь ласка, увійдіть в систему');
      return;
    }

    const payload = parseJwt(token);
    const userId = payload?.id || payload?.userId;
    if (!userId) {
      alert('Не вдалося визначити ваш ідентифікатор користувача');
      return;
    }

    try {
      await createDoctorProfile(form, token, Number(userId));
      alert('Профіль створено успішно!');
    } catch (err: any) {
      console.error('Помилка створення профілю:', err);
      alert(err.message || 'Сталася помилка');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="education"
        placeholder="Освіта"
        value={form.education}
        onChange={handleChange}
      />
      <input
        name="experience"
        placeholder="Досвід"
        value={form.experience}
        onChange={handleChange}
      />
      <textarea
        name="bio"
        placeholder="Про себе"
        value={form.bio}
        onChange={handleChange}
      />
      <input
        name="photoUrl"
        placeholder="Фото URL (опціонально)"
        value={form.photoUrl}
        onChange={handleChange}
      />
      <button type="submit">Створити профіль</button>
    </form>
  );
};

export default CreateDoctorProfileForm;
