import React, { useState } from 'react';
import { Link } from 'react-router-dom';


interface Props {
  id?: number;
  role: 'admin' | 'doctor' | 'patient' | null;
  onLogout: () => void;
  isAuthenticated: boolean;
  firstName?: string;
  lastName?: string;
}

const Navbar: React.FC<Props> = ({ id, role, onLogout, isAuthenticated, firstName, lastName }) => {
  const [showAuthModal, setShowAuthModal] = useState(false);

  return (
    <>
      <nav className="bg-primary text-background px-6 py-3 flex items-center justify-between">
        <div className="space-x-6 font-semibold text-lg">
          <Link to="/services" className="hover:underline">Послуги</Link>
          <Link to="/doctors" className="hover:underline">Лікарі</Link>

          {role === 'doctor' && (
            <Link to={`/doctors/${id}`} className="hover:underline">Мій профіль</Link>
          )}

          {role === 'admin' && (
            <>
              <Link to="/admin/services" className="hover:underline">Адмін: Послуги</Link>
              <Link to="/admin/categories" className="hover:underline">Адмін: Категорії</Link>
            </>
          )}

          {isAuthenticated && (
            <Link to="/appointments" className="hover:underline">Записи</Link>
          )}
        </div>

        <div className="space-x-4 flex items-center">
          {isAuthenticated ? (
            <>
              <Link to={`/user/profile/${id}`} className="font-semibold hover:underline">{firstName} {lastName}</Link>
              <button
                onClick={onLogout}
                className="bg-blue-500 hover:bg-blue-700 px-4 py-2 rounded-md font-semibold transition"
              >
                Вийти
              </button>
            </>
          ) : (
            <button
              onClick={() => setShowAuthModal(true)}
              className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md font-semibold transition"
            >
              Вхід / Реєстрація
            </button>
          )}
        </div>
      </nav>

    </>
  );
};

export default Navbar;
