// src/api/appointmentApi.ts
import axios from 'axios';

const API = 'http://localhost:5000/api/services/appointments';

interface AppointmentData {
  doctorId: number;
  serviceId: number;
  date: string;
}

interface UpdateAppointmentData {
  date?: string;
  status?: string;
}

export const createAppointment = async (
  data: AppointmentData,
  token: string
) => {
  const response = await axios.post(API, data, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return response.data; // повертаємо лише дані
};

export const updateAppointment = async (
  id: number,
  data: UpdateAppointmentData,
  token: string
) => {
  const response = await axios.put(`${API}/${id}`, data, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return response.data; // повертаємо лише дані
};

export const getBookedSlots = async (doctorId: number, serviceId: number, date: string, token: string) => {
  return axios.get(`http://localhost:5000/api/services/appointments/booked`, {
    params: { doctorId, serviceId, date },
    headers: { Authorization: `Bearer ${token}` },
  });
};
