// File: src/App.tsx
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Додаємо Router та Routes
import Register from './components/auth/RegisterForm';
import Login from './components/auth/LoginForm';
import AdminServicesPage from './pages/AdminServicesPage'; // Сторінка для адміністраторів
import BookAppointmentPage from './pages/BookAppointmentPage'; // Сторінка для бронювання

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken); // при перезавантаженні підтягнемо токен
    }
  }, []);

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <Router>
      <div>
        <h1>Clinic Booking System</h1>

        {/* Перевірка токену */}
        {!token ? (
          <>
            <Register />
            <Login setToken={setToken} />
          </>
        ) : (
          <>
            <h2>Welcome back!</h2>
            <button onClick={handleLogout}>Logout</button>
            {/* Створюємо маршрути */}
            <Routes>
              {/* Для адміністраторів */}
              <Route path="/admin" element={<AdminServicesPage />} />
              {/* Для бронювання */}
              <Route path="/book-appointment" element={<BookAppointmentPage />} />
            </Routes>
          </>
        )}
      </div>
    </Router>
  );
};

export default App;
